uLecture.controller('lectureController', function($scope, $location, $routeParams, lectureModel) {
  $scope.lectures = lectureModel.lectures(1);
  $scope.addLecture = function() {
    lectureModel.addLecture({
      url: $routeParams.url,
      name: $routeParams.name
    });
    return $location.path('/manageLectures/');
  };
  return 1;
});

uLecture.controller('commentController', function($scope, $location, $routeParams, $resource, lectureModel) {
  var lecture, name;

  lecture = $routeParams.lectureId;
  name = $routeParams.lectureName;
  $scope.isTeacher = function() {
    return true;
  };
  $scope.name = name;
  $scope.comments = lectureModel.commentCRUD(lecture, 'all');
  $scope.addComment = function() {
    lectureModel.addComment({
      promise: $scope.comments,
      newComment: $scope.comment,
      lecture: $routeParams.lectureId
    });
    return $location.path('/comments/' + lecture + '/' + name);
  };
  return 1;
});

